 a--; b--; // decrement for float
  System.out.println(a);
 System.out.println(b);
 
 int c=78555; int d=123454; // local variable initialization and declaration.
 System.out.println(c);
 System.out.println(d);
 
 c++; d++;  // increment value for int datatype
 System.out.println(c);
 System.out.println(d);
 
 c--; d--; // decrement for int
 System.out.println(c);
 System.out.println(d); 
 
 byte e= 50; byte f= 120;
 
 /* System.out.println(e);
 System.out.println(f); */
 f++; e++;
 
 System.out.println(e);
