package operator;

public class Unarywithsymbol {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int  i =10;
  int b =20;
  boolean c= false;
  boolean d=true;
  System.out.println(i);
  System.out.println(b);
  System.out.println(c);
  System.out.println(d);
  System.out.println(!c);
  System.out.println(!d);// 
  System.out.println(~i); //
  System.out.println(~b); //
	}

}